use rand::{rngs::ThreadRng, seq::SliceRandom, Rng, distributions::Alphanumeric};

pub fn generate_email() -> String {
   return get_random_adjective()
       +&get_random_noun()
       +&generate_random_i8().to_string()
       +"@"
       +&get_random_domain();
}
pub fn generate_bollocks(string_size :i32) -> String {
    rand::thread_rng()
        .sample_iter(&Alphanumeric)
        .take(string_size as usize)
        .map(char::from)
        .collect()
}

pub fn generate_random_i8() -> i8 {
    let mut rng = rand::thread_rng();
    rng.gen::<i8>()
}
pub fn generate_random_i16() -> i16 {
    let mut rng = rand::thread_rng();
    rng.gen::<i16>()
}
pub fn generate_random_i32() -> i32 {
    let mut rng = rand::thread_rng();
    rng.gen::<i32>()
}
pub fn generate_name() -> String {
    get_random_firstname()+" "+&get_random_lastname()
}

